import React, { useState } from 'react';
import './progress.css';


const Progress = ({done}) => {
	const [style, setStyle] = React.useState({});
	
	setTimeout(() => {
		const newStyle = {
			opacity: 1,
			width: `${done}%`
		}
		
		setStyle(newStyle);
	}, 200);
	
	return (
        <>
		<div className="progress">
			<div className="progress-done" style={style}>
				<p>HTML5</p>{done}%
			</div>
            
		</div>
        <div className="progress">
        <div className="progress-done" style={style}>
            <p>HTML5</p>{done}%
        </div>
        
    </div>
    </>
	)
}

const Main = () => (
	<>
        

        <div className="progressmain">
		<Progress done="99"/>
		<Progress done="80"/>
        
        </div>
        
	</>
);
export default Main;
