import React, { useState } from 'react';
import './progress.css';


const Progress = ({done}) => {
	const [style, setStyle] = React.useState({});
	
	setTimeout(() => {
		const newStyle = {
			opacity: 1,
			width: `${done}%`
		}
		
		setStyle(newStyle);
	}, 200);
	
	return (
		<div className="progress">
			<div className="progress-done" style={style}>
				{done}%
			</div>
		</div>
	)
}

const Main = () => (
	<>
        <div className="progressmain">
		<h1>React Progress Bar</h1>
		<Progress done="70"/>
        <h1>React Progress Bar</h1>
		<Progress done="70"/>
        
        </div>
	</>
);
export default Main;
