import React, { useEffect  } from 'react';
import {Button} from 'react-mdl';
import "../../node_modules/bootstrap/dist/css/bootstrap.css";
import "../../node_modules/animate.css";
import '../App.css';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faGem, faClock, faStar } from '@fortawesome/free-solid-svg-icons';
import Main from './progressbar';
import Aos from "aos";
import "aos/dist/aos.css";
import Typical from 'react-typical';
import $ from "jquery";



const LandingPage = () =>{
  useEffect(() => {
      Aos.init({duration:2000});
      
  }, []);
  $(window).on('load', function() {
    Aos.init();
  });        
        return( 
            <>            
            <section  className="landing-grid" style={{width: '100%', margin:'auto'}}>
                <div className="overrlay">

               <div className=" flex1"> 
               <h6 className="animate__animated animate__flash animate__repeat-3 animate__slow">Hi!</h6>
                <h3 className="animate__animated animate__bounce animate__repeat-3 animate__slow">Here I'm Hafiz Awais!</h3>
                <p>
                    <Typical 
                    loop={Infinity}
                    wrapper="b"
                    steps={[
                        'web Developer',
                        2000,
                        'web Designer',
                        2000,
                        'Database Manager',
                        2000,
            
                    ]}
                    />
                </p>
                </div>
                
                <div className="flex2">
                <img data-aos="fade-up" data-aos-anchor=".other-element" src={process.env.PUBLIC_URL+"awaispic.jpg"} alt="my-pic"
                className="rounded-circle"/>
         </div>
                 <div data-aos="fade-left" data-aos-anchor=".other-element" className="flex3">
                 <Button raised accent ripple className="btton">Higher me!</Button>
                 </div>
                 </div>
                
            </section>
            <div  className="paragraph ovrrlayy" >
                     <p className="container" data-aos="fade-up" data-aos-anchor=".other-element">Lorem ipsum, or lipsum as it is sometimes known, is dummy
                      text used in laying 
                      </p>
                      <p data-aos="fade-up" data-aos-anchor=".other-element">out print, graphic or web designs. 
                         The passage is attributed to an unknown typesetter</p>
                         <p data-aos="fade-up" data-aos-anchor=".other-element"> in the 15th 
                         century who is thought 
                         
                         to have scrambled
                         
                          parts of Cicero's De 
                         Finibus </p>
                         <p data-aos="fade-up" data-aos-anchor=".other-element">Bonorum et Malorum for use in a type specimen book.</p>
                     
                <div  className="icons container" data-aos="fade-up">
                <FontAwesomeIcon  className="icns" icon={faGem} data-aos="fade-up" data-aos-anchor=".other-element"/>
               <FontAwesomeIcon  className="icns" icon={faClock} data-aos="fade-up" data-aos-anchor=".other-element"/>
               <FontAwesomeIcon className="icns" icon={faStar} data-aos="fade-up" data-aos-anchor=".other-element"/>
               

                
                 </div>
                 <div className="sub-heading container">
                     <h6>first rating</h6>
                     <h6>first rating</h6>
                     <h6>first rating</h6>


                 </div>
                 </div>
                 
                 <div className="badge">
                     <h1>Skills</h1>
                 <Main/>
                 
            </div>
           </>
        );
           
};
export default LandingPage;
