import React, { useEffect  } from 'react';
import {Button} from 'react-mdl';
import "../../node_modules/bootstrap/dist/css/bootstrap.css";
import "../../node_modules/animate.css";
import '../App.css';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faGem, faClock, faStar } from '@fortawesome/free-solid-svg-icons';
import Main from './progressbar';
import Aos from "aos";
import "aos/dist/aos.css";



const LandingPage = () =>{
  useEffect(() => {
      Aos.init({duration:2000});
      Aos.refresh();
  }, []);        
        return( 
            <>            
            <section  className="landing-grid" style={{width: '100%', margin:'auto'}}>
                <div className="overrlay">

               <div className=" flex1"> 
               <h6 className="animate__animated animate__flash animate__repeat-3 animate__slow">Hi!</h6>
                <h1 className="animate__animated animate__bounce animate__repeat-3 animate__slow">Here I'm Hafiz Awais!<span className="texts animate__animated animate__pulse animate__repeat-3 animate__slow"><br></br> Web Developer!</span></h1>
                </div>
                
                <div className="flex2">
                <img data-aos="fade-up" src={process.env.PUBLIC_URL+"awaispic.jpg"} alt="my-pic"
                className="rounded-circle"/>
         </div>
                 <div data-aos="fade-left" className="flex3">
                 <Button raised accent ripple className="btton">Higher me!</Button>
                 </div>
                 </div>
                
            </section>
            <div  className="paragraph ovrrlayy">
                     <p className="container">Lorem ipsum, or lipsum as it is sometimes known, is dummy
                      text used in laying 
                      </p>
                      <p>out print, graphic or web designs. 
                         The passage is attributed to an unknown typesetter</p>
                         <p> in the 15th 
                         century who is thought 
                         
                         to have scrambled
                         
                          parts of Cicero's De 
                         Finibus </p>
                         <p>Bonorum et Malorum for use in a type specimen book.</p>
                     
                <div  className="icons container">
                <FontAwesomeIcon  className="icns" icon={faGem}/>
               <FontAwesomeIcon  className="icns" icon={faClock}/>
               <FontAwesomeIcon className="icns" icon={faStar}/>
               

                
                 </div>
                 <div className="sub-heading container">
                     <h6>first rating</h6>
                     <h6>first rating</h6>
                     <h6>first rating</h6>


                 </div>
                 </div>
                 
                 <div className="badge">
                     <h1>Skills</h1>
                 <Main/>
                 
            </div>
           </>
        );
           
};
export default LandingPage;
