import React, { useState } from 'react';
import './progress.css';


const Progress = ({done}) => {
	const [style, setStyle] = React.useState({});
	
	setTimeout(() => {
		const newStyle = {
			opacity: 1,
			width: `${done}%`
		}
		
		setStyle(newStyle);
	}, 200);
	
	return (
		<div className="progress">
			<div className="progress-done" style={style}>
				{done}%
			</div>
            
		</div>
        
	)
}

const Main = () => (
	<>
	<div className="mainclass">
        <div className="progressmain">
		<h6>HTML5</h6>
		<Progress done="99"/>

		</div>

        <div className="progressmain">
		<h6>CSS3</h6>
		
		<Progress done="99"/>
        </div>
        <div className="progressmain">
		<h6>Bootstrap4</h6>
		<Progress done="80"/>
		</div>

        <div className="progressmain">
		<h6>jQuery</h6>
		<Progress done="90"/>        
		</div>
        <div className="progressmain">
		<h6>JavaScript</h6>
		<h6>Reactjs</h6>
		</div>

        <div className="progressmain">
		<Progress done="80"/>
		<Progress done="70"/>
        </div>
        </div>
	</>
);
export default Main;
