import React, {Component} from 'react';
import {makeStyles} from '@material-ui/core/styles';
import { Typography , Box} from '@material-ui/core';

const useStyles = makeStyles(theme=>({

        minContainer:{
            background:'#262626'
        },
        timeLine:{
            position: "relative",
            padding: "1rem",
            margin: "0 auto",
            "&:before":{
                content: "''",
                position:"absolute",
                height: "100%",
                border: "1px solid tan",
                right: "40px",
                top: 0
            },
            "&:after":{
                content: "''",
                display: "table",
                clear: "both",
            },
            [theme.breakpoints.up("md")]:{
                padding:"2rem",
            "&:before":{
                left: "calc(50% - 1px)",
                right: "auto",
            },
            }
        
},
       timeLineYear:{
           textAlign: "center",
            maxWidth: "9.375rem",
            margin: "0 3rem 0 auto",
            fontSize: "1.8rem",
            background: "tomato",
            color: "white",
            lineHeight: 1,
            padding: "0.5 rem 0 1rem",
            "&:before":{
                display: "none"
            },
            [theme.breakpoints.up("md")]:{
                textAlign: "center",
                margin:"0 auto",
                "&:nth-of-type(2n)":{
                    float: "none",
                   margin: "0 auto", 
                },
                "&:nth-of-type(2n):before":{

                    display: "none",
                }
            }
       }
}));

const Resume =()=>{
    const classes = useStyles();
    
        return(
           <div>
          <Box component="header" className={classes.minContainer}>
              <Typography variant = "h4" align="center">
                working Experience  
                  </Typography> 
          <Box component="div" className={classes.timeLine}>
          <Typography variant = "h2" className={classes.timeLineYear}>
              2013
              </Typography>
              </Box>
                  
              </Box> 
           </div>
        
        
        );
};
export default Resume;
